class PasswordGenerator{static lcase="abcdefghijklmnopqrstuvwxyz";static ucase="ABCDEFGHIJKLMNOPQRSTUVWXYZ";static numb="1234567890";static symbols="!@#$%?&*";static randRange(t,a){var e=a-t,s=Math.ceil(Math.log2(e)/8);if(!s)return t;for(var n=Math.pow(256,s),r=new Uint8Array(s);;){window.crypto.getRandomValues(r);for(var i=0,h=0;h<s;h++)i=(i<<8)+r[h];if(i<n-n%e)return t+i%e}}static generate(t=10,a=!0,e=!0,s=!0){let n="";for(let r=0;r<t;r++)n+=this.lcase.charAt(this.randRange(0,this.lcase.length));if(n=n.split(""),a){let i=this.randRange(1,Math.floor(t/2)+1);for(let h=0;h<i;h++)n[this.randRange(0,n.length)]=this.ucase.charAt(this.randRange(0,this.ucase.length))}if(e){let l=this.randRange(1,Math.floor(t/2)+1);for(let g=0;g<l;g++)n[this.randRange(0,n.length)]=this.numb.charAt(this.randRange(0,this.numb.length))}if(s){let o=this.randRange(1,3);for(let c=0;c<o;c++)n[this.randRange(0,n.length)]=this.symbols.charAt(this.randRange(0,this.symbols.length))}return n.join("")}}